import React, { Component } from 'react'
import { BrowserRouter as Router, Route, Link } from "react-router-dom";
import Home from './home'
import { connect } from 'react-redux'
import axios from 'axios'
import Filtre from './filter'
import { Redirect } from "react-router-dom";
import BookName from './bookName'


class navbar extends Component {


    constructor(props) {
        super(props);
        this.state = {
            list: "",
            genre: "none",
            gender:"none",
            redirect:false

        };


    }
    renderRedirect = () => {
        if (this.state.redirect) {
          return  <Redirect to='/filters' />
        }
    }


    componentDidMount() {
        this.setState({
            redirect:false
        })
        axios.get("http://localhost:8080/a.json")
            .then(resp => {
                this.getBookList(resp.data)


            }).catch(err => {
                console.log(err);
            })
            console.log("teste");

    }



    getBookList(e) {
        const action = { type: 'GET_LIST', value: e }
        this.props.dispatch(action)

    }


    select=e=>{
        this.setState({genre:e.target.value})
        console.log("state",this.state.genre);
    }


    select2=(e)=>{
        this.setState({gender:e.target.value})
        console.log("state",this.state.genre);
    }


    filtrer=(e)=> {
        const action = { type: 'FILTER', value: e }
        this.props.dispatch(action)
        this.setState({
            redirect:true
        })

    }


    render() {
        return (
            <Router>
                <div>
                    {this.renderRedirect()}

                    <nav class="navbar navbar-expand-lg navbar-light bg-light">
                        <a class="navbar-brand" href="#">Navbar</a>
                        <button class="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarNavAltMarkup" aria-controls="navbarNavAltMarkup" aria-expanded="false" aria-label="Toggle navigation">
                            <span class="navbar-toggler-icon"></span>
                        </button>
                        <div class="collapse navbar-collapse" id="navbarNavAltMarkup">
                            <div class="navbar-nav">
                                <Link class="nav-item nav-link active" to="/vcv">Home <span class="sr-only">(current)</span></Link>

                            </div>
                        </div>
                    </nav>
                    <div class="wrapper">

                        <nav id="sidebar">
                            <div class="sidebar-header">
                                <h3>Sort by</h3>
                            </div>

                            <ul class="list-unstyled components">

                                <li>
                                    <Link to="/book">Book​ ​ Name</Link>
                                </li>
                                <li>
                                    <Link to="/" data-toggle="collapse" aria-expanded="false" >Author​ ​ Name</Link>

                                </li>

                            </ul>
                        </nav>

                    </div>
                    <div >
                    <div >

                        <form onSubmit={(e)=>{
                            e.preventDefault()
                            this.filtrer({
                                genre:this.state.genre,
                                gender:this.state.gender
                            })

                        }}>
                            <div class="form-group" className="col-md-6">

                                <label for="exampleFormControlSelect1">Filter​ ​ By​ ​ Book​ ​ Genre</label>
                                <select class="form-control" id="exampleFormControlSelect1"  value={this.state.genre} onChange={this.select}>
                                    <option value="none">none</option>
                                    {this.props.genre.length > 0 ? this.props.genre.map(re => {
                                        return (

                                            <option value={re} >{re}</option>

                                        )
                                    }) : ""}
                                </select>



                            </div>
                            <div class="form-group" className="col-md-6">
                                <label for="exampleFormControlSelect1">Filter​ ​ By​ ​ Author​ ​ Gender</label>
                                <select class="form-control" id="exampleFormControlSelect2" name="gender" value={this.state.gender} onChange={this.select2}>
                                    <option value="none">none</option>
                                    <option value="man">man</option>
                                    <option value="woman">woman</option>

                                </select>
                            </div>

                            <input type="submit" className="btn btn-primary" value="search" style={{marginTop:"10px"}}/>
                        </form>
                    </div>

                </div>
                    <div className="container">
                        <Route path="/"  exact component={Home}></Route>
                        <Route path="/filters" component={Filtre}></Route>
                        <Route path="/book" component={BookName}></Route>
                    </div>


                </div>
            </Router>

        )
    }
}

const mapStateToProps = (state) => {
    return {
        bookList: state.bookList,
        genre: state.genre,
        filtre:state.filtre
    }
}
export default connect(mapStateToProps)(navbar)
