const initialState = {
  bookList: "",
  genre: ""

}

function product(state = initialState, action) {


  switch (action.type) {
    case 'GET_LIST':
      var gen = []
      var test = true
      for (let i = 0; i < action.value.length; i++) {
        for (let j = 0; j < action.value.length; j++) {
          if (action.value[i] == action.value[j] && i !== j) {
            test = false
            break
          }
        }
        if (test) {
          gen.push(action.value[i].genre)
          console.log("genre", action.value[i].genre);

        }

      }

      state = { ...state, bookList: action.value }
      console.log("connecter", state.bookList);
      return state



    default:
      return state
  }
}

export default product
