import React, { Component } from 'react'
import { connect } from 'react-redux'
import axios from 'axios'
import { Redirect } from "react-router-dom";
import FilterComp from'./filtreComp'



class home extends Component {



    componentDidMount() {
        this.setState({
            redirect:false
        })
        axios.get("http://localhost:8080/a.json")
            .then(resp => {
                this.getBookList(resp.data)


            }).catch(err => {
                console.log(err);
            })
            console.log("teste");

    }





    render() {
        return (


            <div>
                <FilterComp />



                {this.props.bookList.length > 0 ? <div className="row">

                    {this.props.bookList.map(res => {
                        return (
                            <center>
                                <div >
                                    <div className="col-12" style={{ marginTop: "10px" }}>
                                        <div class="card">
                                            <div class="card-body">
                                                <h4 class="card-title"> Name: {res.name}</h4>
                                                <p class="card-text">Genre :{res.genre}</p>
                                                <p class="card-text">Author: {res.author.name}({res.author.gender})</p>
                                                <p class="card-text">publish date :{res.date}</p>
                                            </div>
                                        </div>
                                    </div>

                                </div>
                            </center>


                        )
                    })}
                </div> : ""}
            </div>
        )
    }
}

const mapStateToProps = (state) => {
    return {
        bookList: state.bookList,
        genre: state.genre
    }
}
export default connect(mapStateToProps)(home)
