import React, { Component } from 'react'
import { connect } from 'react-redux'
import axios from 'axios'
import { Redirect } from "react-router-dom";
import FilterComp from './filtreComp'



class home extends Component {

    constructor(props) {
        super(props);
        this.state = {
            list: "",
        };
    }




    componentDidMount() {
        axios.get("http://localhost:8080/a.json")
            .then(resp => {
                this.getBookList(resp.data)
                this.setState({
                    list: this.props.bookList
                })
                console.log("props", this.state.list);
            }).catch(err => {
                console.log(err);
            })


        this.redirection()

    }

    getBookList(e) {
        const action = { type: 'GET_LIST', value: e }
        this.props.dispatch(action)

    }
    redirection() {
        const action = { type: 'REDIRECTION', value: "" }
        this.props.dispatch(action)

    }
    sortByBookName = () => {
        this.setState({
            list: this.state.list.sort((a, b) => {
                var x = a.name.toLowerCase()
                var y = b.name.toLowerCase()
                if (x < y) {
                    return -1
                } else if (x > y) {
                    return +1
                }
                return 0
            })
        })
    }
    sortByAuthorName = () => {
        this.setState({
            list: this.state.list.sort((a, b) => {
                var x = a.author.name.toLowerCase()
                var y = b.author.name.toLowerCase()
                if (x < y) {
                    return -1
                } else if (x > y) {
                    return +1
                }
                return 0
            })
        })
    }

    render() {
        return (


            <div>



                <ul class="list-unstyled components">

                    <li>
                        <button onClick={e => {
                            e.preventDefault()
                            this.sortByBookName()
                        }} style={{ marginBottom: "10px" }} className="btn btn-primary">Sort by Book​​ Name</button>
                    </li>
                    <li>
                        <button onClick={e => {
                            e.preventDefault()
                            this.sortByAuthorName()
                        }} style={{ marginBottom: "10px" }} className="btn btn-primary">Sort by author name</button>
                    </li>

                </ul>










                <div>
                    <div>
                        <form onSubmit={(e) => {
                            e.preventDefault()
                            this.filtrer({
                                genre: this.state.genre,
                                gender: this.state.gender
                            })

                        }}>
                            <div class="form-group" className="col-md-6">
                                <h3>Filter​ ​ By​ </h3>
                                <label for="exampleFormControlSelect1">​ Book​ ​ Genre</label>
                                <select class="form-control" id="exampleFormControlSelect1" value={this.state.genre} onChange={this.select}>
                                    <option value="none">none</option>
                                    {this.props.genre.length > 0 ? this.props.genre.map(re => {
                                        return (

                                            <option value={re} >{re}</option>

                                        )
                                    }) : ""}
                                </select>



                            </div>
                            <div class="form-group" className="col-md-6">
                                <label for="exampleFormControlSelect1"> Author​ ​ Gender</label>
                                <select class="form-control" id="exampleFormControlSelect2" name="gender" value={this.state.gender} onChange={this.select2}>
                                    <option value="none">none</option>
                                    <option value="man">man</option>
                                    <option value="woman">woman</option>

                                </select>
                            </div>

                            <input type="submit" className="btn btn-primary" value="search" style={{ marginTop: "10px" }} />
                        </form>
                    </div>

                </div>








                {this.state.list.length > 0 ? <div className="row">

                    {this.state.list.map(res => {
                        return (
                            <center>
                                <div >
                                    <div className="col-12" style={{ marginTop: "10px" }}>
                                        <div class="card">
                                            <div class="card-body">
                                                <h4 class="card-title"> Name: {res.name}</h4>
                                                <p class="card-text">Genre :{res.genre}</p>
                                                <p class="card-text">Author: {res.author.name}({res.author.gender})</p>
                                                <p class="card-text">publish date :{res.date}</p>
                                            </div>
                                        </div>
                                    </div>

                                </div>
                            </center>


                        )
                    })}
                </div> : ""}
            </div>
        )
    }
}

const mapStateToProps = (state) => {
    return {
        bookList: state.bookList,
        genre: state.genre
    }
}
export default connect(mapStateToProps)(home)
