import React, { Component } from 'react'
import { connect } from 'react-redux'
import FilterComp from'./filtreComp'

class filter extends Component {

    render() {
        return (
            <div>
                <FilterComp />

                {this.props.filtre.length > 0 ? <div className="row">

                    {this.props.filtre.map(res => {
                        return (
                            <center>
                                <div >
                                    <div className="col-12" style={{ marginTop: "10px" }}>
                                        <div class="card">
                                            <div class="card-body">
                                                <h4 class="card-title"> Name: {res.name}</h4>
                                                <p class="card-text">Genre :{res.genre}</p>
                                                <p class="card-text">Author: {res.author.name}({res.author.gender})</p>
                                                <p class="card-text">publish date :{res.date}</p>
                                            </div>
                                        </div>
                                    </div>

                                </div>
                            </center>


                        )
                    })}
                </div> : ""}
            </div>
        )
    }
}
const mapStateToProps = (state) => {
    return {
        filtre: state.filtre
    }
}
export default connect(mapStateToProps)(filter)
