import React, { Component } from 'react'
import { connect } from 'react-redux'
import axios from 'axios'
import { Redirect } from "react-router-dom";
import FilterComp from './filtreComp'



class home extends Component {

    constructor(props) {
        super(props);
        this.state = {
            list: "",
        };
    }




    componentDidMount() {
        axios.get("http://localhost:8080/a.json")
            .then(resp => {
                this.getBookList(resp.data)
                this.setState({
                    list: this.props.bookList
                })
                console.log("props", this.state.list);
            }).catch(err => {
                console.log(err);
            })


        this.redirection()

    }

    getBookList(e) {
        const action = { type: 'GET_LIST', value: e }
        this.props.dispatch(action)

    }
    redirection() {
        const action = { type: 'REDIRECTION', value: "" }
        this.props.dispatch(action)

    }


    render() {
        return (


            <div>



                <ul class="list-unstyled components">

                    <li>
                        <button onClick={e=>{
                            this.sortByBookName()
                        }}>Sort by Book​​ Name</button>
                    </li>
                    <li>
                        <Link to="/authorName" >Author​ ​ Name</Link>

                    </li>

                </ul>

                <FilterComp />



                {this.state.list.length > 0 ? <div className="row">

                    {this.state.list.map(res => {
                        return (
                            <center>
                                <div >
                                    <div className="col-12" style={{ marginTop: "10px" }}>
                                        <div class="card">
                                            <div class="card-body">
                                                <h4 class="card-title"> Name: {res.name}</h4>
                                                <p class="card-text">Genre :{res.genre}</p>
                                                <p class="card-text">Author: {res.author.name}({res.author.gender})</p>
                                                <p class="card-text">publish date :{res.date}</p>
                                            </div>
                                        </div>
                                    </div>

                                </div>
                            </center>


                        )
                    })}
                </div> : ""}
            </div>
        )
    }
}

const mapStateToProps = (state) => {
    return {
        bookList: state.bookList,
        genre: state.genre
    }
}
export default connect(mapStateToProps)(home)
