import React, { Component } from 'react'
import { connect } from 'react-redux'
import axios from 'axios'

class authorName extends Component {


    constructor(props) {
        super(props);
        this.state = {
            list: "",
        };


    }


    componentDidMount() {

        if(this.props.bookList){

            this.setState({
                list:this.props.bookList.sort((a, b) => {
                    var x = a.author.name.toLowerCase()
                    var y = b.author.name.toLowerCase()
                    if (x < y) {
                        return -1
                    } else if (x > y) {
                        return +1
                    }
                    return 0
                })
            })
        }else{

            axios.get("http://localhost:8080/a.json")
            .then(resp => {
                this.getBookList(resp.data)
                this.setState({
                    list:this.props.bookList.sort((a, b) => {
                        var x = a.author.name.toLowerCase()
                        var y = b.author.name.toLowerCase()
                        if (x < y) {
                            return -1
                        } else if (x > y) {
                            return +1
                        }
                        return 0
                    })
                })


            }).catch(err => {
                console.log(err);
            })


        }


    }
    getBookList(e) {
        const action = { type: 'GET_LIST', value: e }
        this.props.dispatch(action)

    }



    render() {
        return (
            <div className="row">
                {this.state.list.length>0?this.state.list.map(res=>{
                    return (
                        <center>
                        <div >
                            <div className="col-12" style={{ marginTop: "10px" }}>
                                <div class="card">
                                    <div class="card-body">
                                        <h4 class="card-title"> Name: {res.name}</h4>
                                        <p class="card-text">Genre :{res.genre}</p>
                                        <p class="card-text">Author: {res.author.name}({res.author.gender})</p>
                                        <p class="card-text">publish date :{res.date}</p>
                                    </div>
                                </div>
                            </div>

                        </div>
                    </center>
                    )
                }):""}
            </div>
        )
    }
}

const mapStateToProps = (state) => {
    return {
        bookList: state.bookList,
        genre: state.genre,
        filtre: state.filtre
    }
}
export default connect(mapStateToProps)(authorName)
