import React, { Component } from 'react'
import { connect } from 'react-redux'

class bookName extends Component {


    constructor(props) {
        super(props);
        this.state = {
            list: "",
        };


    }


    componentDidMount() {
        if(this.props.bookList){
            this.setState({
                list:this.props.bookList.sort((a, b) => {
                    var x = a.name.toLowerCase()
                    var y = b.name.toLowerCase()
                    if (x < y) {
                        return 1
                    } else if (x > y) {
                        return -1
                    }
                    return 0
                })
            })
        }


    }


    render() {
        return (
            <div>
                {/* {this.props.bookList.sort((a,b)=>{
                    var x = a.name.toLowerCase()
                    var y = b.name.toLowerCase()
                    if(x<y){
                        return 1
                    }else return -1
                })} */}
            </div>
        )
    }
}

const mapStateToProps = (state) => {
    return {
        bookList: state.bookList,
        genre: state.genre,
        filtre: state.filtre
    }
}
export default connect(mapStateToProps)(bookName)
