import React, { Component } from 'react'
import { connect } from 'react-redux'
import axios from 'axios'
import { Redirect } from "react-router-dom";


class filtreComp extends Component {


    constructor(props) {
        super(props);
        this.state = {
            list: "",
            genre: "none",
            gender: "none",
            redirect: ""
        };


    }
    renderRedirect = () => {
        if (this.state.redirect && this.props.redirection !== "/filters") {
            return <Redirect to='/filters' />
        }
    }


    componentDidMount() {
        this.setState({
            redirect: false
        })
        console.log("lien", this.props.redirection);



    }



    getBookList(e) {
        const action = { type: 'GET_LIST', value: e }
        this.props.dispatch(action)

    }


    select = e => {
        this.setState({ genre: e.target.value })
        console.log("state", this.state.genre);
    }


    select2 = (e) => {
        this.setState({ gender: e.target.value })
        console.log("state", this.state.genre);
    }


    filtrer = (e) => {
        const action = { type: 'FILTER', value: e }
        this.props.dispatch(action)
        this.setState({
            redirect: true
        })

    }




    render() {
        return (
            <div>{this.renderRedirect()}
                <div>
                    <form onSubmit={(e) => {
                        e.preventDefault()
                        this.filtrer({
                            genre: this.state.genre,
                            gender: this.state.gender
                        })

                    }}>
                        <div class="form-group" className="col-md-6">
                            <p>Filter​ ​ By​ </p>
                            <label for="exampleFormControlSelect1">​ Book​ ​ Genre</label>
                            <select class="form-control" id="exampleFormControlSelect1" value={this.state.genre} onChange={this.select}>
                                <option value="none">none</option>
                                {this.props.genre.length > 0 ? this.props.genre.map(re => {
                                    return (

                                        <option value={re} >{re}</option>

                                    )
                                }) : ""}
                            </select>



                        </div>
                        <div class="form-group" className="col-md-6">
                            <label for="exampleFormControlSelect1">Filter​ ​ By​ ​ Author​ ​ Gender</label>
                            <select class="form-control" id="exampleFormControlSelect2" name="gender" value={this.state.gender} onChange={this.select2}>
                                <option value="none">none</option>
                                <option value="man">man</option>
                                <option value="woman">woman</option>

                            </select>
                        </div>

                        <input type="submit" className="btn btn-primary" value="search" style={{ marginTop: "10px" }} />
                    </form>
                </div>

            </div>
        )
    }
}
const mapStateToProps = (state) => {
    return {
        bookList: state.bookList,
        genre: state.genre,
        filtre: state.filtre,
        redirection: state.redirection
    }
}
export default connect(mapStateToProps)(filtreComp)
