import React, { Component } from 'react'
import { connect } from 'react-redux'
import axios from 'axios'


class home extends Component {
    constructor(props) {
        super(props);
        this.state = {
            list: "",

        };

    }
    componentDidMount() {
        axios.get("http://localhost:8080/a.json")
            .then(resp => {
                this.getBookList(resp.data)


            }).catch(err => {
                console.log(err);

            })
    }

    getBookList(e) {
        const action = { type: 'GET_LIST', value: e }
        this.props.dispatch(action)

    }
    render() {
        return (


            <div>
                <div >
                    <div >
                        <div class="form-group" className="col-md-6">
                            <label for="exampleFormControlSelect1">Filter​ ​ By​ ​ Book​ ​ Genre</label>
                            <select class="form-control" id="exampleFormControlSelect1">
                                {this.props.genre.length>0?this.props.genre.map(re=>{
                                    return (

                                            <option>{re}</option>

                                    )
                                }):""}
                            </select>
                        </div>
                        <div class="form-group" className="col-md-6">
                            <label for="exampleFormControlSelect1">Filter​ ​ By​ ​ Author​ ​ Gender</label>
                            <select class="form-control" id="exampleFormControlSelect1">
                                <option>none</option>
                                <option>man</option>
                                <option>woman</option>

                            </select>
                        </div>
                    </div>

                </div>

                {this.props.bookList.length > 0 ? <div className="row">

                    {this.props.bookList.map(res => {
                        return (
                            <center>
                                <div >
                                    <div className="col-md-12" style={{ marginTop: "10px" }}>
                                        <div class="card">
                                            <div class="card-body">
                                                <h4 class="card-title"> Name: {res.name}</h4>
                                                <p class="card-text">Genre :{res.genre}</p>
                                                <p class="card-text">Author: {res.author.name}({res.author.gender})</p>
                                                <p class="card-text">publish date :{res.date}</p>
                                            </div>
                                        </div>
                                    </div>

                                </div>
                            </center>


                        )
                    })}
                </div> : ""}
            </div>
        )
    }
}

const mapStateToProps = (state) => {
    return {
        bookList: state.bookList
    }
}
export default connect(mapStateToProps)(home)
